import React, { useEffect } from "react";
import "./App.css";
import { getNASAPictures } from "./NasaAPI";
import moment from "moment";

function App() {
  const [pictures, updatePictures] = React.useState(null);
  const [dateOfFilter, setDateOfFilter] = React.useState(null);
  useEffect(() => {
    if (!pictures) {
      // const startDate = new Date("2020-07-01T08:28:41.917Z");
      const startDate = new Date("2022-02-20T08:28:41.917Z");
      const endDate = new Date();
      getNASAPictures(startDate, endDate).then((res) => {
        console.log("startDate is", startDate);
        console.log("endDate is", endDate);
        console.log("res is", res);
        let filtered = res.filter((image) => image.media_type === "image");
        updatePictures(spliter(filtered, 3));
        const sevenDaysBefore = moment().subtract(7, "days");
        const today = moment();
        console.log("sevenDaysBefore is", sevenDaysBefore.toDate());
        console.log(
          moment(pictures[0][0].date).isBetween(sevenDaysBefore, today)
        );
      });
    }
  }, [pictures]);
  useEffect(() => {
    const sevenDaysBefore = moment().subtract(7, "days");
    const today = moment();
    console.log("sevenDaysBefore is", sevenDaysBefore.toDate());
    // console.log("pictures[0][0].date ", pictures[0]);
    // console.log(moment(pictures[0][0].date).isBetween(sevenDaysBefore, today));
  }, [dateOfFilter]);
  let subArrays = [];
  function spliter(array, chunkSize) {
    for (let i = 0; i < array.length; i += chunkSize) {
      const chunk = array.slice(i, i + chunkSize);
      // do whatever
      console.log();
      subArrays.push(chunk);
    }
    return subArrays;
  }
  let subArrayRes = pictures && spliter(pictures, 3);
  subArrayRes && console.log("subArrays are", subArrayRes);
  return (
    <div className="container App" style={{ marginTop: 20 }}>
      <div>
        <select
          class="form-select"
          aria-label="Default select example"
          onChange={(v) => {
            console.log("value is", v.target.value);
          }}
        >
          <option selected>Open this select menu</option>
          <option value="week">last week</option>
          <option value="twoWeeks">last two weeks</option>
          <option value="month">last month</option>
        </select>
      </div>

      {pictures &&
        pictures.map((pictureArr) => (
          <div className="row my-4">
            <div className="col-md-4">
              <div className="imageBox">
                <p className="imageBoxDate">{pictureArr[0]?.date}</p>
                <img
                  style={{ width: "100%", height: 250 }}
                  src={pictureArr[0].url}
                />
                <div class="d-flex justify-content-between p-3">
                  <h5>{pictureArr[1]?.title}</h5>
                  <span>{pictureArr[0]?.copyright || ""}</span>
                </div>
              </div>
            </div>
            <div className="col-md-4">
              <div className="imageBox">
                <p className="imageBoxDate">{pictureArr[1]?.date}</p>
                <img
                  style={{ width: "100%", height: 250 }}
                  src={pictureArr[1]?.url}
                />
                <div class="d-flex justify-content-between p-3">
                  <h5>{pictureArr[1]?.title}</h5>
                  <span>{pictureArr[1]?.copyright || ""}</span>
                </div>
              </div>
            </div>
            <div className="col-md-4">
              <div className="imageBox">
                <p className="imageBoxDate">{pictureArr[2]?.date}</p>
                <img
                  style={{ width: "100%", height: 250 }}
                  src={pictureArr[2]?.url}
                />
                <div class="d-flex justify-content-between p-3">
                  <h5>{pictureArr[2]?.title}</h5>
                  <span>{pictureArr[2]?.copyright || ""}</span>
                </div>
              </div>
            </div>
          </div>
        ))}
    </div>
  );
}

export default App;
