import React, { useEffect } from "react";
import "./App.css";
import { getNASAPictures } from "./NasaAPI";

function App() {
  const [pictures, updatePictures] = React.useState(null);
  console.log("pictures are", pictures);
  useEffect(() => {
    if (!pictures) {
      // const startDate = new Date("2020-07-01T08:28:41.917Z");
      const startDate = new Date("2022-02-20T08:28:41.917Z");
      const endDate = new Date();
      getNASAPictures(startDate, endDate).then((res) => {
        console.log("startDate is", startDate);
        console.log("endDate is", endDate);
        console.log("res is", res);
        updatePictures(res.filter((image) => image.media_type === "image"));
      });
    }
  }, [pictures]);
  let subArrays = [];
  function spliter(array, chunkSize) {
    for (let i = 0; i < array.length; i += chunkSize) {
      const chunk = array.slice(i, i + chunkSize);
      // do whatever
      console.log();
      subArrays.push(chunk);
    }
    return subArrays;
  }
  let subArrayRes = pictures && spliter(pictures, 3);
  subArrayRes && console.log("subArrays are", subArrayRes);
  return (
    <div className="App" style={{ marginTop: 20 }}>
      {subArrayRes &&
        subArrayRes.map((pictureArr) => (
          <div className="row" style={{ marginBottom: 10 }}>
            <div className="col">
              <img
                style={{ width: "100%", height: 300 }}
                src={pictureArr[0].url}
              />
            </div>
            <div className="col border-danger border-bottom">
              <p
                style={{
                  position: "absolute",
                  top: 10,
                  backgroundColor: "red",
                  left: 10,
                }}
              >
                asdasdasd
              </p>
              <img
                style={{ width: "100%", height: 250 }}
                src={pictureArr[1] && pictureArr[1].url}
              />
              <div>{pictureArr[1]?.title}</div>
            </div>
            <div className="col">
              <img
                style={{ width: "100%", height: 300 }}
                src={pictureArr[2] && pictureArr[2].url}
              />
            </div>
          </div>
        ))}
      {/* <div class="row">
        <div class="col">col</div>
        <div class="col">col</div>
        <div class="col">col</div>
      </div> */}
      {/* {pictures &&
        pictures.map((picture) => (
          <div className="col-md-12">
            <div className="col-md-2" key={picture.date}>
              <img src={picture.url} />
              <button type="button" class="btn btn-primary">
                Primary
              </button>
              <button type="button" class="btn btn-warning">
                Warning
              </button>
            </div>
          </div>
        ))} */}
      <p>{process.env.REACT_APP_NASA_API_KEY}</p>
    </div>
  );
}

export default App;
